const pg = require('pg');

const cs = 'postgres://postgres:postgres@localhost:5432/slam_dunk_analytics_db';

const client = new pg.Client(cs);

client.connect();

var combined = []

client.query("SELECT * FROM combined").then(res => {

    const data = res.rows;

    data.forEach(row => console.log(row))
    data.forEach(row => combined.push(row))
    //console.log(data)

    require('fs').writeFile(

        './combined.json',  //If you want you can rename this and edit path in app.js
    
        JSON.stringify(combined),
    
        function (err) {
            if (err) {
                console.error('Crap happens');
            }
        }
    );

}).catch(err => {
    console.log(err.stack);
}).finally(() => {
    client.end()
});

